import Loader from '../Components/Loader/Loader';
import axios from 'axios';

import {Component} from 'react';

class CookieDetailsForm extends Component{
    constructor(props){
        super(props);
        this.state = {
            cookieName: '',
            description: '',
            moreDetails: '',
            category: this.props.categories[0],
            quantity: null,
            price: null
        }
        this.handleNameChange = this.handleNameChange.bind(this);  
    }

    handleNameChange(fieldName, e){
        this.setState({...this.state, [fieldName]: e.target.value});
    }
    
    render(){
        let categoryOptions = this.props.categories.map((val)=>{
            return(
                <option key={val}>{val}</option>
            );
        });
        return(
            <div className= "cookie-detail-form alert alert-light alert-dismissible fade show position-fixed top-0 col-12 h-100 overflow-scroll" role="alert">
                <div className= "d-flex flex-column col-12 text-center">
                    <h4>Add New Cookie</h4>
                    <div><hr/></div>
                    <div className= "d-flex justify-content-center text-start">
                        <form className= "col-6" form-light>
                            <div className="mb-3">
                                <label for="inputCookieName" className="form-label">Name</label>
                                <input type="text" className="form-control" id="inputCookieName" placeholder="Enter cookie name" onChange={(e)=>this.handleNameChange('cookieName', e)} value= {this.state.cookieName}/>
                            </div>
                            <div className="mb-3">
                                <label for="inputDescription" className="form-label">Description</label>
                                <input type="text" className="form-control" id="inputDescription" placeholder="Enter short description" onChange={(e)=>this.handleNameChange('description', e)} value= {this.state.description}/>
                            </div>
                            <div className="mb-3">
                                <label for="inputMoreDetails" className="form-label">More Details</label>
                                <textarea type="text" className="form-control" id="inputMoreDetails" placeholder="Enter more details (eg. ingredients)" onChange={(e)=>this.handleNameChange('moreDetails', e)} value= {this.state.moreDetails}/>
                            </div>
                            <div className="mb-3">
                                <label for="selectCategory" className="form-label">Select Category</label>
                                <select className="form-select" id= "selectCategory" aria-label="Select category" value={this.state.category}>
                                    {categoryOptions}
                                </select>
                            </div>
                            <div className="mb-3">
                                <label for="inputAvailableQuantity" className="form-label">Available Quantity</label>
                                <input type="number" className="form-control" id="inputAvailableQuantity" placeholder="Enter quantity in stock"  onChange={(e)=>this.handleNameChange('quantity', e)} value= {this.state.quantity}/>
                            </div>
                            <div className="mb-3">
                                <label for="inputPrice" className="form-label">Price</label>
                                <input type="number" className="form-control" id="inputPrice" placeholder="Enter Price"  onChange={(e)=>this.handleNameChange('price', e)} value= {this.state.price}/>
                            </div>
                            <button type="submit" className="btn btn-dark">Submit</button>
                        </form>
                    </div>
                </div>
                <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close" onClick= {this.props.onCloseHandler}></button>
            </div>
        );
    }
}

export default CookieDetailsForm;
