// import Loader from '../Components/Loader/Loader';
import axios from 'axios';
import {incShowLoaderCount, decShowLoaderCount, setError} from '../Redux/Reducers/GlobalData/ActionCreater'; 
import {connect} from 'react-redux';
import {Component} from 'react';

class CookieDetailsForm extends Component{
    constructor(props){
        super(props);
        this.state = {
            cookieName: '',
            description: '',
            moreDetails: '',
            category: this.props.cookieCategories[0],
            quantity: '',
            price: ''
        }
        this.handleNameChange = this.handleNameChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this); 
    }

    handleNameChange(fieldName, e){
        this.setState({...this.state, [fieldName]: e.target.value});
    }

    addCookieUrl = "http://localhost:3001/cookie/add";

    handleSubmit(e){
        e.preventDefault();
        this.props.incShowLoaderCount();
        axios.post(this.addCookieUrl, {
            NAME: this.state.cookieName,
            DESCRIPTION: this.state.description,
            MORE_DETAILS: this.state.moreDetails,
            CATEGORY: this.state.category,
            QUANTITY_IN_STOCK: parseInt(this.state.quantity),
            PRICE_RS: parseInt(this.state.price)
        },{
           timeout: 5000 
        }).then((res)=>{
            if(res.code === 200){
                //request for cookies data
            }
        }).catch((err)=>{
            this.props.setError(err.message);
            console.log(err);
        }).finally(()=>{
            this.props.decShowLoaderCount();
            this.props.onCloseHandler();
        });
    }
    
    render(){
        let categoryOptions = this.props.cookieCategories.map((val)=>{
            return(
                <option key={val}>{val}</option>
            );
        });
        return(
            <div className= "cookie-detail-form alert alert-light alert-dismissible fade show fixed-top col-12 h-100 overflow-scroll" role="alert">
                <div className= "d-flex flex-column col-12 text-center">
                    <h4>Add New Cookie</h4>
                    <div><hr/></div>
                    <div className= "d-flex justify-content-center text-start">
                        <form className= "col-6 form-light" onSubmit={this.handleSubmit}>
                            <div className="mb-3">
                                <label htmlFor="inputCookieName" className="form-label">Name</label>
                                <input type="text" className="form-control" id="inputCookieName" placeholder="Enter cookie name" onChange={(e)=>this.handleNameChange('cookieName', e)} value= {this.state.cookieName}/>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="inputDescription" className="form-label">Description</label>
                                <input type="text" className="form-control" id="inputDescription" placeholder="Enter short description" onChange={(e)=>this.handleNameChange('description', e)} value= {this.state.description}/>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="inputMoreDetails" className="form-label">More Details</label>
                                <textarea type="text" className="form-control" id="inputMoreDetails" placeholder="Enter more details (eg. ingredients)" onChange={(e)=>this.handleNameChange('moreDetails', e)} value= {this.state.moreDetails}/>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="selectCategory" className="form-label">Select Category</label>
                                <select className="form-select" id= "selectCategory" aria-label="Select category" onChange={(e)=>this.handleNameChange('category', e)} value={this.state.category}>
                                    {categoryOptions}
                                </select>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="inputAvailableQuantity" className="form-label">Available Quantity</label>
                                <input type="number" className="form-control" id="inputAvailableQuantity" placeholder="Enter quantity in stock"  onChange={(e)=>this.handleNameChange('quantity', e)} value= {this.state.quantity}/>
                            </div>
                            <div className="mb-3">
                                <label htmlFor="inputPrice" className="form-label">Price</label>
                                <input type="number" className="form-control" id="inputPrice" placeholder="Enter Price"  onChange={(e)=>this.handleNameChange('price', e)} value= {this.state.price}/>
                            </div>
                            <button type="submit" className="btn btn-dark">Submit</button>
                        </form>
                    </div>
                </div>
                <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close" onClick= {this.props.onCloseHandler}></button>
            </div>
        );
    }
}
function mapStateToProps(state){
    return({
        cookieCategories: state.globalData.cookieCategories,
    });
}

export default connect(mapStateToProps, {incShowLoaderCount,decShowLoaderCount,setError})(CookieDetailsForm);
