// import './CategoryField.css'
import {connect} from 'react-redux';
import {useEffect, useState} from 'react';

function CategoryField(props){
    let listItems = props.categories.map((val)=>{
        return(
            <li className='nav-item' key= {val+"_li"}>
                <a className="nav-link" href={"#"+val.replace(/\s+/g, '').trim()} key={val}>{val}</a>
            </li>
        );
    });
    return(
        <nav id="navbar2" className="navbar fixed-top navbar-light bg-light px-3" style={{top: props.headerHeight}} role="navigation">
            <ul className="nav nav-pills col-12 nav-fill nav-justified">
                {listItems}
            </ul>
        </nav>
    )
}

function mapStateToProps(state){
    return{
        headerHeight: state.globalStyle.headerHeight
    }
}

export default connect(mapStateToProps)(CategoryField);
