import './CategoryField.css'
import {useEffect, useState} from 'react';

function CategoryField(props){
    let [headerHeight, setHeaderHeight] = useState(0);
    let appHeader={offsetHeight:null};
    useEffect(()=>{
        appHeader = document.getElementById('appHeader');
        setHeaderHeight(appHeader.offsetHeight);
    },[appHeader.offsetHeight]);
    let listItems = props.categories.map((val)=>{
        return(
            <li>
                <a className="nav-link" href={"#"+val} key={val}>{val}</a>
            </li>
        );
    });
    return(
        <nav id="navbar2" className="navbar fixed-top navbar-light bg-light px-3" style={{top: headerHeight}} role="navigation">
            <ul className="nav nav-pills col-12 nav-fill nav-justified">
                {listItems}
            </ul>
        </nav>
    )
}

export default CategoryField;
