import Header from './Header/Header';
import Body from './Body/Body';
import Loader from './Components/Loader/Loader';
import CookieDetailsForm from './Components/CookieDetailsForm/CookieDetailsForm';
import $ from 'jquery';
import Popper from 'popper.js';
import axios from 'axios';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import './css/main.min.css';

import {useState, useEffect} from 'react';

function App() {
  const getCategoriesUrl = 'http://localhost:3001/cookie/categories';

  let [isShowLoader, setShowLoader] = useState(true);
  let[categories, setCategories] = useState([]);

  useEffect(()=>{
    axios.get(getCategoriesUrl, {
        timeout: 5000
    })
    .then((res)=>{
        setTimeout(()=>{
            setShowLoader(false);
            setCategories(res.data.categoryNames);
        }, 2000);
    })
    .catch((e)=>{
        console.log(e.message);
        setShowLoader(false);
    });
  }, []);
  if(isShowLoader){
    return(
        <Loader coverScreen= {false}/>
    );
  }
  else{
    return (
      <div>
        <Header/>
        <Body categories={categories}/>
        <CookieDetailsForm categories={categories}/>
      </div>
    );
  }
}

export default App;
