import CookieCard from '../Cookie_Card/CookieCard';
import CategoryField from '../Components/CategoryField/CategoryField';
import Loader from '../Components/Loader/Loader';
import addIcon from '../resources/add.svg';
import axios from 'axios';

import {useState, useEffect} from 'react';

import './Body.css';

function Body(props){
    const getCategoriesUrl = 'http://localhost:3001/cookie/categories';

    let [isShowLoader, setShowLoader] = useState(true);
    let[categories, setCategories] = useState([]);
    useEffect(()=>{
        axios.get(getCategoriesUrl, {
            timeout: 5000
        })
        .then((res)=>{
            setShowLoader(false);
            setCategories(res.data.categoryNames);
        })
        .catch((e)=>{
            console.log(e.message);
            setShowLoader(false);
        });
    }, []);
    if(isShowLoader){
        return(
            <Loader coverScreen= {false}/>
        );
    }
    else{
        let categoryFields = categories.map((val)=>{
            return(
                <CategoryField categoryName = {val}/>
            );
        });
        return(
            <div className= 'div-body'>
                <categoryFields/>
                <div className= 'div-cards'>
                    <CookieCard cookieName={'cookie1'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie2'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie3'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie4'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie5'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    {/* <CookieCard cookieName={'cookie6'} price= {100} description= {'This cookie is made with love and dedication.'}/> */}
                </div>
                <CategoryField categoryName = 'Category 2'/>
                <div className= 'div-cards'>
                    <CookieCard cookieName={'cookie1'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie2'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie3'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie4'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    <CookieCard cookieName={'cookie5'} price= {100} description= {'This cookie is made with love and dedication.'}/>
                    {/* <CookieCard cookieName={'cookie6'} price= {100} description= {'This cookie is made with love and dedication.'}/> */}
                </div>
                <div className= 'div-add-button'>
                    <a href= 'localhost:3000' className= 'add-link'>
                        <img className= 'add-icon' src= {addIcon} alt= 'add'/>
                    </a>
                </div>
            </div>
        );
    }
    
}

export default Body;
