import './Cards.css';
import CookieCard from '../CookieCard/CookieCard';
import axios from 'axios';

import {useState, useEffect} from 'react';

function Cards(props){
    const getCookiesUrl = 'http://localhost:3001/cookie/getAll';

    let [cookies, setCookies] = useState([]);
    useEffect(()=>{
        axios.get(getCookiesUrl, {
            timeout: 5000
        })
        .then((res)=>{
            setCookies(res.data);
        })
        .catch((e)=>{
            console.log(e.message);
        });
    }, [props]);

    let cookieCards = cookies.map((val)=>{
        return(
            <CookieCard cookieName= {val.NAME} description= {val.DESCRIPTION} price= {val.PRICE_RS} key= {val._id}/>
        );
    });

    return(
        <div className= 'div-cards'>
            {cookieCards}
        </div>
    );
}

export default Cards;
