import {aTypeGetCookieData, aTypeSetShowLoader, aTypeGetCookieCategories} from './ActionCreater';

let initialState = {
    cookieData: [],
    cookieCategories: [],
    isShowLoader: true
}

function globalDataReducer(state = initialState, action){
    switch(action.type){
        case aTypeGetCookieData:{
            return {...state, cookieData: action.val};
        }
        case aTypeSetShowLoader:{
            return {...state, isShowLoader: action.val};
        }
        case aTypeGetCookieCategories:{
            return {...state, cookieCategories: action.val};
        }
        default:{
            return state;
        }
    }
}

export default globalDataReducer;
