import CategoryField from '../Components/CategoryField/CategoryField';
import Cards from '../Cards/Cards';
import CookieDetailsForm from '../CookieDetailsForm/CookieDetailsForm';
import AddNewCookieBtn from '../Components/AddNewCookieBtn/AddNewCookieBtn';
import Loader from '../Components/Loader/Loader';

import axios from 'axios';
import {useState, useEffect} from 'react';

import './Body.css';

function Body(props){
    const getCategoriesUrl = 'http://localhost:3001/cookie/categories';

    let [isShowLoader, setShowLoader] = useState(true);
    let[categories, setCategories] = useState([]);
  
    useEffect(()=>{
      axios.get(getCategoriesUrl, {
          timeout: 5000
      })
      .then((res)=>{
          setTimeout(()=>{
              setShowLoader(false);
              setCategories(res.data.categoryNames);
          }, 2000);
      })
      .catch((e)=>{
          console.log(e.message);
          setShowLoader(false);
      });
    }, []);
    if(isShowLoader){
      return(
          <Loader coverScreen= {false}/>
      );
    }
    else{
        let categoryFields = categories.map((val)=>{
            return(
                <CategoryField categoryName = {val} key= {val}>{<Cards/>}</CategoryField>
            );
        });
        return(
            <div className= 'div-body'>
                {categoryFields}
                <AddNewCookieBtn/>
            </div>
        );
    }
}

export default Body;
