import CategoryField from '../Components/CategoryField/CategoryField';
import Cards from '../Cards/Cards';
import CookieDetailsForm from '../CookieDetailsForm/CookieDetailsForm';
import AddNewCookieBtn from '../Components/AddNewCookieBtn/AddNewCookieBtn';
import Loader from '../Components/Loader/Loader';

import axios from 'axios';
import {useState, useEffect} from 'react';

// import './Body.css';

function Body(props){
    const getCategoriesUrl = 'http://localhost:3001/cookie/categories';

    let [isShowLoader, setShowLoader] = useState(true);
    let [categories, setCategories] = useState([]);
    let [isShowAddCookieForm, setShowAddCookieForm] = useState(false);
  
    useEffect(()=>{
      axios.get(getCategoriesUrl, {
          timeout: 5000
      })
      .then((res)=>{
          setTimeout(()=>{
              setShowLoader(false);
              setCategories(res.data.categoryNames);
          }, 2000);
      })
      .catch((err)=>{
          console.log(err.message);
          setShowLoader(false);
      });
    }, []);

    function handleAddNewCookieForm(isShowForm){
        setShowAddCookieForm(isShowForm);
    }

    if(isShowLoader){
      return(
          <Loader coverScreen= {false}/>
      );
    }
    else{
        return(
            <div className= 'div-body'>
                <CategoryField categoryName = {val} key= {val}>{<Cards/>}</CategoryField>
                <AddNewCookieBtn onClickHandler = {()=>handleAddNewCookieForm(true)}/>
                {
                    isShowAddCookieForm?
                    <CookieDetailsForm categories= {categories}
                    onCloseHandler= {()=>handleAddNewCookieForm(false)}
                    showLoader= {(boolVal)=>setShowLoader(boolVal)}/>
                    :null
                }
            </div>
        );
    }
}

export default Body;
