// import './CategoryField.css'
import {connect} from 'react-redux';
import {updateCategoryFieldHeight} from '../../Redux/Reducers/GlobalStyle/ActionCreater';
import {useEffect, useState} from 'react';

function CategoryField(props){
    let categoryFieldId = 'categoryField';
    useEffect(()=>{
        let categoryFieldBar = document.getElementById(categoryFieldId);
        props.updateCategoryFieldHeight(categoryFieldBar.offsetHeight);
    });
    let listItems = props.categories.map((val)=>{
        return(
            <li className='nav-item' key= {val+"_li"}>
                <a className="nav-link" href={"#"+val.replace(/\s+/g, '').trim()} key={val}>{val}</a>
            </li>
        );
    });
    return(
        <nav className="navbar fixed-top navbar-light bg-white px-3 d-flex flex-column flex-lg-row" style={{top: props.headerHeight}} role="navigation" id={categoryFieldId}>
            <ul className="nav nav-pills col-3 col-lg-12 nav-fill nav-justified">
                {listItems}
            </ul>
        </nav>
    )
}

function mapStateToProps(state){
    return{
        headerHeight: state.globalStyle.headerHeight
    }
}

export default connect(mapStateToProps, {updateCategoryFieldHeight})(CategoryField);
