import NavLinks from './NavLinks/NavLinks'
import SearchForm from './SearchForm/SearchForm';
import CategoryField from '../Components/CategoryField/CategoryField';
import LoginSignUpBtns from './LoginSignUpBtns/LoginSignUpBtns';
import BrandName from './BrandName/BrandName';
import NavbarToggleBtn from './NavbarToggleBtn/NavbarToggleBtn';
import {actionUpdateHeaderHeight} from '../Redux/Reducers/GlobalStyle/ActionCreater';
import {connect} from 'react-redux';
import {useEffect} from 'react';

function Header(props){
    const headerId = "appHeader";
    useEffect(()=>{
        let appHeader = document.getElementById(headerId);
        props.actionUpdateHeaderHeight(appHeader.offsetHeight);
    })
    return(
        <header className='fixed-top' id={headerId}>
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
                <div className="container-fluid">
                    <BrandName/>
                    <NavbarToggleBtn/>
                    <div className="collapse navbar-collapse" id="navbarSupportedContent">
                        <SearchForm/>
                        <NavLinks/>
                        <LoginSignUpBtns/>
                    </div>
                </div>
            </nav>
            {/* <CategoryField categories = {props.categories}/> */}
        </header>
    );
}

export default connect(null, {actionUpdateHeaderHeight})(Header);
