import Header from './Header/Header';
import Body from './Body/Body';
import Loader from './Components/Loader/Loader';
import axios from 'axios';
import {getCookieCategoriesAsync, setShowLoader} from './Redux/Reducers/GlobalData/ActionCreater';
import {connect} from 'react-redux';
// import '../node_modules/@popperjs/core/dist/umd/popper.min.js'
// import 'popper.js';
// import $ from 'jquery';
// import 'bootstrap/dist/js/bootstrap.bundle'
import './css/main.min.css';
import {useState, useEffect} from 'react';

function App(props) {
  useEffect(()=>{
    props.getCookieCategoriesAsync();
  }, [props]);
  return (
    <div>
      {isShowLoader? <Loader coverScreen= {false}/>: null}
      <Header categories= {props.cookieCategories}/>
      <Body categories= {props.cookieCategories} handleLoader = {null}/>
    </div>
    );
}

function mapStateToProps(state){
  return({
      cookieCategories: state.globalData.cookieCategories,
      isShowLoader: state.globalData.isShowLoader
  });
}

export default connect(mapStateToProps,{getCookieCategoriesAsync, setShowLoader})(App);
